mapper.insert:
         /* post.addPost */
         INSERT INTO 
           POST (
   				USER_ID,
				POST_CONT,
				FST_REG_YMD,
				LST_MOD_YMD
				)
		 VALUES (
				#{user_id},
				#{post_cont},
				#{fst_reg_ymd},
				#{lst_mod_ymd}
				);

         /* post.addAtchFile */
         INSERT INTO 
  POST_ATCH_IMG (
   				POST_NO,
				RL_FILE_NM,
				SAVE_FILE_NM,
				FILE_SZ
				)
		 VALUES (
				#{post_no},
				#{rl_file_nm},
				#{save_file_nm},
				#{file_sz}
				);

mapper.update:
         /* post.modifyPost */
         UPDATE POST
            SET POST_CONT = #{post_cont},
				LST_MOD_YMD = #{lst_mod_ymd}
		  WHERE POST_NO = #{post_no};

         /* post.deletePost */
         UPDATE POST
            SET USE_YN = 'N'
		  WHERE POST_NO = #{post_no};
