mapper.select:
/* user.isExistUser */
SELECT COUNT(*)
FROM	[USER]
WHERE	USER_ID = #{user_id} 
		AND
		USE_YN = 'Y';

         /* user.isExistFrnd */
		SELECT
			COUNT(*)
		FROM
			user_frnd_mg
		WHERE
			(
				USER_ID = #{user_id}
				AND FRND_ID = #{frnd_id}
			)
			OR (
				USER_ID = #{frnd_id}
				AND FRND_ID = #{user_id}
			)
			AND USE_YN = 'Y';

/* user.isExistUser */
SELECT COUNT(*)
FROM	[USER]
WHERE	USER_ID = #{user_id} 
		AND
		PASSWORD = #{password}
		AND
		USE_YN = 'Y';

/* user.getUserInfo */
SELECT USER_ID,
		USER_NM,
		BYMD,
		EMAIL_ADDR,
		JOIN_YMD
FROM	[USER]
WHERE	USER_ID = #{user_id}
		AND
		USE_YN = 'Y';

mapper.insert:
         /* user.addFriend */
         INSERT INTO 
   USER_FRND_MG (
   				USER_ID,
				FRND_ID,
				APLY_YMDT
				)
		 VALUES (
				#{user_id},
				#{frnd_id},
				#{aply_ymdt}
				)
		     ON DUPLICATE KEY 
		 UPDATE USE_YN = 'Y',
		 		ACCP_YN = 'N',
		 		APLY_YMDT = #{aply_ymdt};

mapper.update:
	 /* user.accpFrnd */
    UPDATE USER_FRND_MG
       SET ACCP_YN = 'Y'
     WHERE USER_ID = #{user_id}
     		AND
     		FRND_ID = #{frnd_id}
